/**
 * check if user has provide the email address 
 * and it exists in local storage
 */
var emailExists = function() {
    var email = localStorage.getItem('userEmailAddress');
    if (email !== undefined && email != null && email != '') {
        emailAddress = email;
        return true;
    }
    //don't need to have email saved to be logged in
    try {
        var apiUrl = "https://"+recentSubdomain.partnerSubdomain+".complinks.co/api/v1/getUserDetail";

        $.post(apiUrl, {})
        .done(function(data) {
            // console.log(data);
            return true;//save email from userDetail call
        })
        .fail(function(data) {
            $('#no-email-alert').show();
            $('.image-2').attr('src','images/icon128.png');
            $('.navbar-brand-co').html('Complinks Rewards Everywhere');
            // put default data in popup
            return false;        
        });    
    } catch (ex) {
        $('#no-email-alert').show();
        $('.image-2').attr('src','images/icon128.png');
        $('.navbar-brand-co').html('Complinks Rewards Everywhere');
        return false;
    }
    


};

/**
 * Show email field in popup if the user   
 * didn't provided it yet otherwise hide it.  
 */
var toggleEmailField = function() {
    if (!emailExists()) {
        $('.loading-icon').hide();
        $('.email-address-div').show();
    } else {
        $('.email-address-div').hide();
    }
};

var openSettings = function() {
    chrome.tabs.create({
        'url': 'chrome://extensions/?options=' + chrome.runtime.id
    });
};
var extractHostname = function(url) {
    var hostname;
    if (url.indexOf("://") > -1) {
        hostname = url.split('/')[2];
    } else {
        hostname = url.split('/')[0];
    }
    hostname = hostname.split(':')[0];
    hostname = hostname.split('?')[0];
    hostname = hostname.replace('www.', '');
    return hostname;
}

var showOffer = function(data) {
    if (!data.isAdvertiser || data.reward == '') {
        return;
    }
    var lsph = localStorage.getItem('primaryHex');
    var lsah = localStorage.getItem('accentHex');

    var html = '<div class="row">';
    html += '<div class="col-xs-12">';
    html += '<buttons class="btn btn-primary activate-btn" style="background-color:'+lsah+'; border-color: '+lsah+';">Activate ' + data.reward + '</buttons>';
    html += '</div></div>';

    $(document).on('click','.activate-btn',function() {
        console.log('activate-btn clicked');
        chrome.tabs.query({
            active: true,
            currentWindow: true
        }, function(tabs) {
            chrome.tabs.sendMessage(tabs[0].id, {
                type: "create-activate-tab",
                url: data.clickUrl
            }, function(response) {
                console.log(response);
                chrome.tabs.query({
                    active: true,
                    currentWindow: true
                }, function(tabs) {
                    if(response.type=="activated") {
                        var html = "";
                        html = '<div class="row">';
                        html += '<div class="col-xs-12">';
                        html += '<buttons class="btn btn-primary activate-btn" style="background-color:green; border-color: green;">' + data.reward + ' Activated!</buttons>';
                        html += '</div></div>';
                        $('.show-offers').html(html);
                    }
                });
            });
        });
    });

    chrome.tabs.query({
        active: true,
        currentWindow: true
    }, function(tabs) {
        chrome.tabs.sendMessage(tabs[0].id, {
            type: "activated?"
        }, function(response) {
            if(response.type=="show") {
                html = "";
                html = '<div class="row">';
                html += '<div class="col-xs-12">';
                html += '<buttons class="btn btn-primary activate-btn" style="background-color:green; border-color: green;">' + data.reward + ' Activated!</buttons>';
                html += '</div></div>';
            }
            $('.show-offers').html(html);
        });
    });
};

/**
 * Send POST request to get offers
 */
var getOffers = function() {
    try {
        var currentDomain = '';
        var apiUrl = "https://"+recentSubdomain.partnerSubdomain+".complinks.co/api/v1/checkDomain";
        var email = localStorage.getItem('userEmailAddress');
        chrome.tabs.query({
            active: true,
            currentWindow: true
        }, function(tabs) {
            if (!tabs || !tabs[0] || !tabs[0].url) {
                return;
            }
            var arr = [];
            currentDomain = extractHostname(tabs[0].url);
            arr.push(currentDomain);
            
            $.post({
                url: apiUrl,
                type: "POST",
                data: JSON.stringify({"domainName":arr}),
                contentType:"application/json",
                dataType:"json"
            })
            .done(function(data) {
                console.log(data[0]);
                showOffer(data[0]);                    
                chrome.tabs.query({
                    active: true,
                    currentWindow: true
                }, function(tabs) {
                    chrome.tabs.sendMessage(tabs[0].id, {
                        type: "activated?"
                    }, function(response) {
                        console.log(response);
                        if(response.type=="show") {
                            $('.activate-btn').css({
                                'background-color': 'green !important',
                                'border-color': 'green !important'
                            });       
                        }
                    });
                });            
            })
            .fail(function(data) {
                callbacks.error(data[0]);
            });
        });
    } catch (ex) {
        // callbacks.error({status: "unauthorized"});
    }
};

/**
 * Close popup window
 */
var cancel = function() {
    window.close();
}

/**
 * Bind onclick events on buttons
 */
var bindEvents = function() {
    $('#cancel').click(cancel);
    $('#settings').click(openSettings);
};

/**
 * Create html to show on extension popup
 */
var buildTimelineHtml = function(events) {
    var html = '';
    var lsph = localStorage.getItem('primaryHex');
    var lsah = localStorage.getItem('accentHex');

    if (events.length < 1) {
        html += '<div class="well well-sm" style="background-color: '+lsph+';">Nothing to show</div>';
        return html;
    }
    $.each(events, function(index, value) {
        if (value && value != '') {
            html += '<div class="well well-sm">' + value + '</div>';
        }
    });
    return html;
};

/**
 * Build html to show error if 
 * API call fails to fetch News
 * For specific email
 */
var getNewsErrorHtml = function() {
    var html = '<div class="alert alert-danger">';
    html += 'Api failed to fetch events for this email</div>';
    return html;
};

/**
 * Get timeline of user activities
 */
var getEvents = function() {
    // if (!emailAddress)
        // return true;
    try {
        $(".loading-icon").show();
        var apiUrl = "https://"+recentSubdomain.partnerSubdomain+".complinks.co/api/v1/getNews";
        $.post(apiUrl, {
                userEmail: emailAddress,
            })
            .done(function(data) {
                // workaround until api doesn't always return 200
                if(data['status'] === 'unauthorized') {
                    $(".loading-icon").hide();
                    $('.timeline-events').html(getNewsErrorHtml());    
                    return false;
                }
                // console.log("Events loaded ", data);
                $(".loading-icon").hide();
                if (data) {
                    var eventHtml = buildTimelineHtml(data);
                    $('.timeline-events').html(eventHtml);
                    var lsph = localStorage.getItem('primaryHex');
                    $('.well-sm, .btn-primary').css({'background-color':lsph});
                    $('.well-sm, .btn-primary').css({'border-color':lsph});
                }
            })
            .fail(function() {
                $(".loading-icon").hide();
                $('.timeline-events').html(getNewsErrorHtml());
            });
    } catch (ex) {
        $(".loading-icon").hide();
        $('.timeline-events').html(getNewsErrorHtml());
    }    
};

var initSubdomain = function() {
    var domains = ['shop','xclub'];
    var loggedIn = [];
    domains.forEach(function(domain) {
        var apiUrl = "https://"+domain+".complinks.co/api/v1/getUserDetail";
  
        $.post(apiUrl, {})
        .done(function(data) {
            if(typeof data.status !== "unauthorized" && typeof data.partnerSubdomain !== "undefined" ) {
                loggedIn.push(data.partnerSubdomain);
                userDetail.push(data);
            }

        });
    });
    return loggedIn;
}

var buildTheme = function() {
    // console.log(recentSubdomain.partnerStyle);
    try {
        var partnerStyle = JSON.parse(recentSubdomain.partnerStyle);
        var primaryName = partnerStyle.primary.name.replace('-','').toLowerCase();
        var primaryHex = primaryName.concat(partnerStyle.primary.main);
        var accentName = partnerStyle.accent.name.replace('-','').toLowerCase();
        var accentHex = accentName.concat(partnerStyle.accent.main);

        localStorage.setItem('primaryHex', colors[primaryHex]);
        localStorage.setItem('accentHex', colors[accentHex]);

        $('.navbar-header-co').css({'background-color':colors[primaryHex]});

        $('.well-sm').css({'background-color':colors[primaryHex]});
        
        chrome.tabs.query({
            active: true,
            currentWindow: true
        }, function(tabs) {
            chrome.tabs.sendMessage(tabs[0].id, {
                type: "activated?"
            }, function(response) {
                console.log(response);
                if(response.type !== "show" && response.type !== null) {
                    $('.activate-btn').css({'background-color':colors[accentHex]});
                    $('.activate-btn').css({'border-color':colors[accentHex]});      
                }
            });
        });  


        $(".activate-btn").hover(function () {
           $(this).animate({'opacity':'0.7'}, 100);
        },function (){
           $(this).animate({'opacity':'1'}, 100);
        });
        $('.avail-points').html(recentSubdomain.availablePoints+' Points');
        localStorage.setItem('availablePoints', recentSubdomain.availablePoints);
        // console.log(colors[primaryHex]);
        $('.image-2').attr('src',recentSubdomain.partnerIcon); //change class in webflow
        //else { Complinks Rewards Everywhere}   images/icon128.png
        $('.navbar-brand-co').html(recentSubdomain.partnerName);
    } catch (ex) {

    }
}

var loggedIn;
var userDetail = [];
var recentSubdomain;


$(document).ready(function() {
    $('.navbar-header-co').click();
    loggedIn = initSubdomain();
    setTimeout(function() {
        try {
            var latestLogin = Math.max.apply(Math,userDetail.map(function(u){
                var ainxs = u.lastLogin.indexOf("(");
                var ainxe = u.lastLogin.indexOf(")");
                var suba = u.lastLogin.substring(ainxs+1,ainxe-1);
                suba = Number(suba);            
                return suba;
            }));
            
            recentSubdomain = userDetail.find(function(u) {
               console.log(typeof u.lastLogin);
               return u.lastLogin.includes(latestLogin); 
            });

            console.log(recentSubdomain);
        } catch(ex) {

        }
        bindEvents();
        toggleEmailField();
        getEvents();
        getOffers();
        buildTheme();
    }, 300);
});